const items = $input.all();
const today = new Date().toLocaleDateString('ko-KR', { timeZone: 'Asia/Seoul' });

// 100자 미만 필터링
const validItems = items.filter(item => {
  const content = item.json.content || '';
  return content.length > 100;
});

if (validItems.length === 0) {
  return [{ 
    json: { 
      exampleUser: "뉴스를 요약해주세요",
      exampleModel: "유효한 뉴스가 없습니다.",
      actualPrompt: "오늘은 요약할 뉴스가 없습니다."
    } 
  }];
}

// Few-shot 예제
const exampleUser = "다음 10개 AI 뉴스 중에서 가장 중요한 3개를 선택하여 요약해주세요:

[뉴스 1] 테슬라 자율주행 업데이트
내용: 테슬라가 FSD 12.0을 출시했다...

[뉴스 2] OpenAI GPT-5 출시
내용: OpenAI가 새로운 GPT-5 모델을 공개...

[뉴스 3] 애플 워치 신제품 할인 이벤트
내용: 애플 워치 시리즈 9 할인...

[뉴스 4] 구글 Gemini 2.0 발표
내용: 구글이 차세대 AI 모델 Gemini 2.0을 발표...

[뉴스 5] 삼성 갤럭시 이벤트
내용: 삼성 갤럭시 예약 판매...";

const exampleModel = "오늘의 AI 뉴스 (2025. 10. 29.)

 [뉴스 1] OpenAI GPT-5 출시

OpenAI가 새로운 GPT-5 모델을 공개했습니다. 이전 버전보다 추론 능력이 크게 향상되었으며, 멀티모달 성능이 개선되었습니다. 기업용 API도 함께 출시되어 다양한 산업에 활용될 전망입니다.

자세히 보기: [링크]

[뉴스 2] 구글 Gemini 2.0 발표

구글이 차세대 AI 모델 Gemini 2.0을 발표했습니다. 실시간 처리 속도가 3배 향상되었고, 코드 생성 정확도가 크게 개선되었습니다. 개발자 베타 버전이 이번 주부터 제공됩니다.

자세히 보기: [링크]

[뉴스 3] 테슬라 자율주행 업데이트

테슬라가 FSD 12.0을 출시했습니다. 딥러닝 기반 의사결정 시스템이 적용되어 안전성이 향상되었습니다. 북미 지역부터 순차적으로 업데이트가 배포됩니다.

자세히 보기: [링크]";

// 실제 프롬프트
let actualPrompt = "오늘의 AI 뉴스 (" + today + ")

";
actualPrompt += "다음 " + validItems.length + "개의 AI 뉴스 중에서 가장 중요한 3개를 선택하여 요약해주세요.

";
actualPrompt += "선택 기준:
";
actualPrompt += "• 기술적 중요도: 새로운 기술, 혁신적 발전
";
actualPrompt += "• 산업 영향력: 실제 비즈니스/사회에 미치는 영향
";
actualPrompt += "• 정보의 구체성: 구체적 데이터, 사실, 수치 포함
";
actualPrompt += "• 광고/홍보성 뉴스는 제외

";
actualPrompt += "⚠️ 중요: 선정된 3개 뉴스는 반드시 [뉴스 1], [뉴스 2], [뉴스 3]으로 순서대로 번호를 매겨주세요.

";
actualPrompt += "위 예제와 같은 형식으로 선정된 3개 뉴스만 요약해주세요.

";
actualPrompt += "뉴스 목록:

";

validItems.forEach((item, i) => {
  let cleanTitle = item.json.title.replace(/\[.*?\d+일.*?\]/g, '').trim();

  actualPrompt += "[뉴스 " + (i+1) + "] " + cleanTitle + "
";
  actualPrompt += "링크: " + item.json.link + "
";
  actualPrompt += "내용: " + item.json.content.substring(0, 400) + "...

";
});

return [{ 
  json: { 
    exampleUser: exampleUser,
    exampleModel: exampleModel,
    actualPrompt: actualPrompt
  } 
}];

